#include <iostream>#include <cmath>#define dist sqrt( (R1-L+R2)*(R1-L+R2) + (R1-C+R2)*(R1-C+R2) )using namespace std; int main(){    int L,C,R1,R2;    while (cin >> L >> C >> R1 >> R2){        if (!L && !C && !R1 && !R2) break;        if ( L < (R1*2) || L < (R2*2) ) cout << "N";        else if (C < R1*2 || C < R2*2 ) cout << "N";        else if ( dist < R1+R2 ) cout << "N";        else cout << "S";        cout << endl;     }    return 0;}